class Solution {
    public int longestAwesome(String s) {
        HashMap<Integer, Integer> map = new HashMap();
        int fHash = 0;
        int n = s.length();
        int res = 0;
        map.put(fHash, -1);
        for(int i=0; i<n; i++){
            int cIndex = s.charAt(i)-'0';
            fHash = fHash^(1<<cIndex);
            res = Math.max(i-map.getOrDefault(fHash, i), res);
            for(int j=0; j<10; j++){
                int posHash = 1<<j;
                int prevHash = fHash^posHash;
                res = Math.max(i-map.getOrDefault(prevHash, i), res);
            }
            map.putIfAbsent(fHash, i);
        }
        return res;
    }
}
