            else if(!st.empty()&&(st.top()>='a'&&st.top()<='z')&&(st.top()-'a')==(s[i]-'A')){
                st.pop();
            }
            else if(!st.empty()&&(st.top()>='A'&&st.top()<='Z')&&(st.top()-'A')==(s[i]-'a')){
                st.pop();
            }
            else{
                st.push(s[i]);
            }
        }
        string res;
        int n=st.size();
        if(n==0) return "";
        for(int i=0;i<n;i++){
            res.push_back(st.top());
            st.pop();
        }
        reverse(res.begin(),res.end());
        return res;
    }
};

