class Solution {
public:
    string makeGood(string s) {
        stack<char> st;
        for(int i=0;i<s.size();i++){
            if(st.empty()) st.push(s[i]);
            else{
                int a=abs(s[i]-st.top());
                if(a==32) st.pop();
                else st.push(s[i]);
        }
        }
        string ans="";
        while(!st.empty()){
            ans.push_back(st.top());
            st.pop();
        }
        int ss=0,e=ans.size()-1;
        while(ss<e){
            swap(ans[ss],ans[e]);
            ss++;
            e--;
        }
        return ans;
    }
};

