use std::collections::VecDeque;
impl Solution {
    pub fn make_good(s: String) -> String {
        if s == "" {
            return s;
        }

        let mut stack = VecDeque::<char>::new();
        let s = s.chars().collect::<Vec<char>>();

        let last = s[0];
        stack.push_back(s[0]);

        for i in 1..s.len() {
            if stack.len() > 0 {
                let last = stack.pop_back().unwrap();
                let is_eq = match last > s[i] {
                    true => {
                        (last as u8) - (s[i] as u8) == 32
                    },
                    false => {
                        (s[i] as u8) - (last as u8) == 32
                    }
                };

                if is_eq {
                    continue;
                }

                stack.push_back(last);
            }

            stack.push_back(s[i]);

        }

        stack.into_iter().collect::<String>()
    }
}
```

