    for i := 0; i < len(s); i++ {
        last := len(res) - 1

        if len(res) == 0 || !isBad(res[last], s[i]) {
            res = append(res, s[i])
        } else {
            res = res[:last]
        }
    }

    return string(res)
}

func isBad(a, b byte) bool {
    if a < b {
        a, b = b, a
    }
    const distance = int('a' - 'A') // 32
    return int(a - b) == distance
}
