class Solution:
    def makeGood(self, s: str) -> str:

        stack = []
        i = 0

        def check(s1, s2):
            return abs(ord(s1)-ord(s2)) == 32

        for i in range(len(s)):
            if not stack:
                stack.append(s[i])
                continue

            l = stack[-1]
            r = s[i]

            if check(l, r):
                stack.pop()
                continue

            stack.append(r)

        return "".join(stack)
