        StringBuilder str = new StringBuilder();
        int size=0;
        for(int i=0;i<s.length();i++){
            size = str.length();
            if(str.length()>0 && Math.abs(s.charAt(i) - str.charAt(size-1))==32) {
                str.deleteCharAt(size-1);
            }
            else{
                str.append(s.charAt(i));
            }
        }
        return str.toString();
    }
}
