			char ans = findKthBit(n - 1, k);//previous row and will be same as before  the mid of current row
			return ans;
		}
		else
		{
			char ans = findKthBit(n - 1, l - k);
			if (ans == '0')
			{
				return '1';
			}
			else
			{
				return '0';
			}
		}
	}
};

