class Solution:
    def __init__(self):
        self.map = {'0': '1', '1': '0'}

    def findKthBit(self, n: int, k: int) -> str:
        mid = 2**(n-1)
        if n == 1 or k == 1: return '0'
        if k == mid: return '1'
        if k < mid: return self.findKthBit(n - 1, k)
        else:
            sbit = self.findKthBit(n - 1, 2**n - k)
            return self.map[sbit]
