class Solution {
public:
    int solve(int n,int k){
        if(n==1&&k==1)
            return 0;
        int mid=pow(2,n)/2;
        if(k<mid)
            return solve(n-1,k);
        else if(k==mid)
            return 1;
        else 
        {
            if(k==(mid+mid/2))
                return !solve(n-1,k-mid);
            else
                return solve(n-1,k-mid);
        }
    }
    char findKthBit(int n, int k) {
        int bit=solve(n,k);
        return (char)(bit+48);    
    }
};
