class Solution {
    public char findKthBit(int n, int k) {
        int total = (int)(Math.pow(2,n)) - 1;
        boolean invert = false;
        while(total > 1){
            if(k == (total+1)/2) return invert == true ? '0' : '1';
            if(k > (total+1)/2){
                k = total - k + 1;
                invert = !invert;
            }
            total = total>>1;
        }
        if(invert)
            return k == 1 ? '1': '0';
        else
            return k == 1 ? '0': '1';
    }
}
