class Solution {
    public char findKthBit(int n, int k) {
        if(n == 1 && k == 1)        //the base case
            return '0';

        int len = (int)Math.pow(2,n)-1;       //length of each level
        int mid = (len+2)/2;

        if(k < mid)         //for k < mid, we just need to look up to the kth bit of n-1th level
            return findKthBit(n-1,k);
		else if(k == mid)         //the mid bit will always be 1 for each level
            return '1';

		//Now for the case, where k > mid
        int prevLen = mid-1;     //length of previous level

        int prevBitPosFromStart = k-mid;     //this will give the position bit whose inverse is the current node's k value
		                                     //but this will be from starting so to obtain it from the end(because it was
											 //reversed), we need to subtract it from the prevLen and then, add one to it 
        return (findKthBit(n-1, prevLen-prevBitPosFromStart+1) == '0'? '1':'0');    
    }
}
