var findKthBit = function(n, k) {
    //dp is the digit array of the formed string, and num is the supposed length 
    let dp = [0], num = 1 
    for(let i = 1; i < k; i++) {
        if(i === num) {  
            num = 2 * num + 1
            dp[i] = 1 
        } else {
            dp[i] = dp[num - 1 - i] ^ 1
        }
    }
    return dp[k - 1] + ''
};
