    def solve(self , n , k) :
        if n == 1 and k == 1 :
            return 0 
        if k == (1 << n) // 2 :
            return 1
        t = ((1 << n )  - 1) // 2 
        if k <= t :
            return self.solve(n - 1, k) 
        return self.solve(n - 1 , (1 << n) - k) ^ 1 

    def findKthBit(self, n: int, k: int) -> str:
        return str(self.solve(n , k))






