class Solution {

    public char findKthBit(int n, int k) {
        return helper(n, k);
    }

    private char helper(int n, int k) {
        if (n == 1) {
            return '0';
        }

        int length = (1 << n) - 1; // Total length of the string for depth n
        int mid = length / 2 + 1; // Middle position

        if (k == mid) {
            return '1';
        } else if (k < mid) {
            return helper(n - 1, k);
        } else {
            char oppositeBit = helper(n - 1, length - k + 1);
            return oppositeBit == '0' ? '1' : '0';
        }
    }
}

