class Solution:
    def findKthBit(self, n: int, k: int) -> str:
        # Map k to indices in binary string s.
        k = k-1
        if k==0:
            return '0'
        # A string at stage n contains (2**n -1) binary digits.
        invert_flag = 0
        length = (1 << (n+1)) - 1
        while length > 1:
            mid = length // 2 # Index of the middle element
            if k==mid:
                # The middle element is always 1.
                return '1' if invert_flag==0 else '0'
            elif k < mid:
                # Focus on returning the k-th element of the first half of string s.
                length = mid
            else:
                # k-th element in back half of string s = 
                # Complement of element (2*mid - k) in first half of string s
                k = 2*mid - k
                length = mid
                # Flip invert flag.
                invert_flag ^= 1
        if invert_flag:
            return '1'
        else:
            return '0'    

