class Solution:
    def findKthBit(self, n: int, k: int) -> str:

        def get_n(n):

            if n == 1:
                return "0"

            previous = get_n(n-1)
            r_invert = "".join(reversed([ "0" if c == "1" else "1" for c in previous]))

