class Solution {
public:
    string invertStr(string s)
    {
        int len = s.length();
        for (int i = 0; i < len; i++)
        {
            if (s[i] == '0')
                s[i] = '1';
            else
                if (s[i] == '1')
                    s[i] = '0';
        }
        return s;
    }
    string reverseStr(string s)
    {
        string str = "";
        int len = s.length();
        for (int i = len - 1; i >= 0; i--)
            str += s[i];
        return str;
    }
    string recursion(string ans, int n, int k)
    {
        if (n == 1)
            return "0";
        return recursion(ans, n - 1, k) + "1" + reverseStr(invertStr(recursion(ans, n - 1, k)));
    }
    char findKthBit(int n, int k) {
        string ans = "";
        string res = recursion(ans, n, k);
        return res[k - 1];
    }
};
```

