public int MaxNonOverlapping(int[] nums, int target)
{
	HashSet<int> visited = new HashSet<int>() { 0 };
	int result = 0;            
	int sum = 0;

	foreach(int num in nums)
	{
		sum += num;

		if (visited.Contains(sum - target))
		{
			result++;
			sum = 0;
			visited.Clear();
		}

		visited.Add(sum);
	}

	return result;
}
