        unordered_set<int> s;

        int sum = 0, i = 0;

        int ans = 0;
        while(i < nums.size()) {
            sum += nums[i];
            if(s.count(sum - target) || sum == target) {
                ans++;
                sum = 0;
                s.clear();
            } else {
                s.insert(sum);
            }
            i++;
        }

        return ans;
    }
};
