```
class Solution {
public:
    int maxNonOverlapping(vector<int>& nums, int target) {

        int n = nums.size();

        int curr_sum = 0;

        int count = 0;

        int end = -1;

        unordered_map<int, int> mp;

        mp[0] = -1;

        for(int i = 0; i < n; i++)
        {
            curr_sum += nums[i];

            int need = curr_sum - target;

            if(mp.count(need) && mp[need] >= end)
            {
