class Solution {
public:
    int maxNonOverlapping(vector<int> &v, int trg) {

    /*  Why greedy Works
        [-----A-----[--]--B-]  [--C--] [--D--] [--E--]
        Lets assume A grows slowly and achive trg is a long time
        Now assuming approach isnt greedy and skip taking A instead lets take B which is shorter thus giving 
        more possiblities for no. of subarrays
        If this happens we get B C D ie ct=3
        If we had taken A instead then it would have finished up at earlier indx than B's end idx
        and thus providing even better chances of higher count
    */      
        unordered_map<int,int> mp;
        int i,sm,n,ct,r,p;
        ct=0;
        r=-1;
        sm=0;
        n=v.size();
        for(i=0;i<n;i++)
        {
           sm+=v[i];
           p=mp[sm-trg]-1;
             if((sm==trg&&r==-1)||(p>=r&&p+1!=0))
             {
                 ct++;
                 r=i;
             }          
            mp[sm]=i+1;
        }
          return ct;
    }   
};
