```
public class MaximumNonOverlappingSubarrayWithTargetSumRecursiveApproach {
    public int maxNonOverlapping(int[] nums, int target) {
        return maxNonOverlapping(0, nums, target);
    }

    private int maxNonOverlapping(int start, int[] nums, int target) {
        if (start >= nums.length) return 0;

        int maxNonOverlap = 0, sum = 0;

        for (int i = start; i < nums.length; i++) {
            sum += nums[i];
            maxNonOverlap = Math.max(maxNonOverlap, maxNonOverlapping(i + 1, nums, target) + (sum == target ? 1 : 0));

            if (sum == target) break;
        }

        return maxNonOverlap;
    }
}
