public class MaximumNonOverlappingSubarrayWithTargetSumMemoizationApproach {
    public int maxNonOverlapping(int[] nums, int target) {
        int[] memo = new int[nums.length];
        Arrays.fill(memo, -1);
        return maxNonOverlapping(0, nums, target, memo);
    }

    private int maxNonOverlapping(int start, int[] nums, int target, int[] memo) {
        if (start >= nums.length) return 0;
        if (memo[start] >= 0) return memo[start];

        int maxNonOverlap = 0, sum = 0;

        for (int i = start; i < nums.length; i++) {
            sum += nums[i];
            maxNonOverlap = Math.max(maxNonOverlap, maxNonOverlapping(i + 1, nums, target, memo) + (sum == target ? 1 : 0));

            if (sum == target) break;
        }

        memo[start] = maxNonOverlap;
        return maxNonOverlap;
    }
}
