 //IDEA: We need to make sure we keep moving the left window to the last sum we counted.
    int maxNonOverlapping(vector<int>& nums, int tar) {
        int n=nums.size();
        int ans=0;

        //create a map to store sum and idx(sum,idx) pair
        unordered_map<int,int> sumCnt;
        sumCnt[0]=-1;

        //rightIdx ptr is the ptr to keep track of our end point of our last subarray having sum target
        int rightIdx=-1;
        int sum=0;

        for(int i=0;i<n;i++){
            sum += nums[i];

            //look for sum-tar in map
            if(sumCnt.count(sum - tar)){
                //(leftIdx+1) is the starting idx of our subarray with sum equals target 
                int leftIdx= sumCnt[sum - tar];
                //check if it is overlapping or not with condn : right <= left

                 //below line is make sure that we don't count a long array which has this subarray inside.
                if(rightIdx <= leftIdx){
                    ans ++;
                    rightIdx = i;//update left window of our subarray
                }
            }

            sumCnt[sum]=i;

        }
        return ans;
    }
