# from functools import lru_cache

class Solution:
    # The problem boils down to finding the maximum number of non overlapping intervals
    # Can be solved using recursion and optimized using memoization
    # Possible TLE
    '''
    @lru_cache(None)
    def maxNonOverlappingIntervals(self, curr_index, last_ending):
        if curr_index == len(self.targets):
            return 0
        do_not_include = self.maxNonOverlappingIntervals(curr_index + 1, last_ending)

        if self.targets[curr_index][0] > last_ending:
            return max(do_not_include, 1 + self.maxNonOverlappingIntervals(curr_index + 1, self.targets[curr_index][1]))
        else:
            return do_not_include
    '''
    # Greedy solution since according to the structure of the problem, the targets will already be sorted
    def maxNonOverlappingIntervals(self, targets):
        last_ending, ans = -1, 0
        for i in range(len(targets)):
            if targets[i][0] > last_ending:
                last_ending = targets[i][1]
                ans += 1
        return ans

    # Keep track of the prefix sums and update the position according to the latest index
    # The latter point is really important because we want the intervals to be as small as possible
    # Just a clever heuristic

    def maxNonOverlapping(self, nums: List[int], target: int) -> int:
        # Prefix sums
        for i in range(1, len(nums)):
            nums[i] += nums[i - 1]
        nums = [0] + nums

        sums_seen_so_far, targets = dict(), []

        # Commented out portion is because we donot want all the possible intervals
        # Instead we want the smallest interval possible

        for i in range(len(nums)):
            if nums[i] - target in sums_seen_so_far:
                for entry in sums_seen_so_far[nums[i] - target]:
                    targets.append((entry, i - 1))
            # if nums[i] not in sums_seen_so_far:
            sums_seen_so_far[nums[i]] = [i]
            # sums_seen_so_far[nums[i]].append(i)

        return self.maxNonOverlappingIntervals(targets)

