    public int maxNonOverlapping(int[] nums, int target)
    {
        int count = 0;
        Map<Integer, List<Integer>> sumIdxesMap = new HashMap<>();
        sumIdxesMap.computeIfAbsent(0, k -> new ArrayList<>()).add(-1);
        int sum = 0;
        int prev = -1;
        for (int i = 0; i < nums.length; i++)
        {
            sum += nums[i];
            List<Integer> idxes = sumIdxesMap.getOrDefault(sum - target, new ArrayList<>());
            if (idxes.size() > 0 && idxes.get(idxes.size() - 1) + 1 > prev)
            {
                count++;
                prev = i;
            }
            sumIdxesMap.computeIfAbsent(sum, k -> new ArrayList<>());
            sumIdxesMap.get(sum).add(i);
        }
        return count;
    }
}
