class Solution:
    def maxNonOverlapping(self, nums: List[int], target: int) -> int:
        # job schedulling
        d={}
        d[0]=-1
        l=[]
        pref=0
        for i in range(len(nums)):
            pref+=nums[i]
            if pref-target in d:
                l.append([d[pref-target]+1,i])
            d[pref]=i
        # l contains starting and end of each possible subarray equal to target
        l.sort(key=lambda x:x[1])
        prev=-2
        ans=0
        for i in l:
            if i[0]>prev:
                ans+=1
                prev=i[1]
        return ans
