class Solution {
public:
    int maxNonOverlapping(vector<int>& nums, int target) {
        int prefix=0; // track prefix sum
        int cnt=0; // total count
        unordered_map<int,int> mp; 
        mp[0]=-1;  // for edge case
        for(int i=0;i<nums.size();i++){
            prefix+=nums[i]; // update prefix sum till that index
            if(mp.find(prefix-target)!=mp.end()){  // if prefix-target find it means it will be answer
                cnt++; // for this increse count
                mp.clear();// now we have solve another problem to get target so clear map
                prefix=0; // set prefixsum=0;
            }
            mp[prefix]++; // otherwise insert in map
        }
        return cnt;
    }
};

