class Solution {
public:
    int maxNonOverlapping(vector<int>& nums, int target) {

        set<int> s;
        int rs = 0;
        int cnt = 0;

        for(int i = 0; i < nums.size(); i++){

            rs+= nums[i];
            if(s.find(rs - target) != s.end() || rs == target){
                s.clear();
                rs = 0;
                cnt++;
            }
            s.insert(rs);
        }
        return cnt;
    }
};
