class Solution {
public:
    int maxNonOverlapping(vector<int>& nums, int target) {
        int leng = nums.size();
        vector<int> dp (leng+1, 0);
        unordered_map<int, int> prefix;
        prefix.emplace(0, 0);
        for (int i=0, s=0; i<leng; i+=1) {
            s += nums[i];
            if (prefix.count(s-target)) dp[i+1] = max(1+dp[prefix[s-target]], dp[i]);
            else dp[i+1] = dp[i];
            prefix[s] = i+1;
        }
        return dp.back();
    }
};
