class Solution:
    def maxNonOverlapping(self, A: List[int], k: int) -> int:
        d = {0 : -1}
        cumsum, res, lastEndIdx = 0, 0, -1
        for i, x in enumerate(A):
            cumsum += x
            if cumsum-k in d and d[cumsum-k] >= lastEndIdx: #startingIdx-1 >= lastEndingIdx
                res += 1
                lastEndIdx = i
            d[cumsum] = i #endingIdx of subarr
        return res
