class Solution:
    def maxNonOverlapping(self, nums: List[int], target: int) -> int:
        seen = {0: -1}
        cur_sum = 0
        res = 0
        last = -1
        for i, num in enumerate(nums):
            cur_sum += num
            if cur_sum - target in seen and seen[cur_sum - target] >= last:
                last = i
                res += 1
            seen[cur_sum] = i
        return res
