class Solution:
    def maxNonOverlapping(self, nums: List[int], target: int) -> int:
        ans = 0
        runningSum = 0
        prefixSum = {0: -1}
        previousRight = -1
        for i, n in enumerate(nums):
            runningSum += n
            if runningSum - target in prefixSum:
                if previousRight <= prefixSum[runningSum - target]:
                    ans += 1
                    previousRight = i
            prefixSum[runningSum] = i
        return ans
    # Time: O(N)
    # Space: O(N)
