class Solution {
    public int res = 0;
    public int maxNonOverlapping(int[] nums, int target) {
        List<int[]> lis = new LinkedList<>();
        for(int i = 0; i < nums.length; i++) {
            int curr = nums[i]; // 
            if(curr == target) {
                lis.add(new int[]{i, i});
                continue;
            }
            for(int j = i + 1; j < nums.length; j++) {
                curr += nums[j];
                if(curr == target) {
                    lis.add(new int[]{i, j});
                    continue;
                }
            }
        }
        if(lis.size() == 0) return 0;

        Collections.sort(lis, (a, b) -> {return a[1] - b[1];});
        int[] curr = lis.get(0);
        res++;
        int end = curr[1];
        for(int i = 1; i < lis.size(); i++) {
            int[] tmp = lis.get(i);
            if(tmp[0] > end) {
                res++;
                end = tmp[1];
            }

        }

        return res;

    }
}
```

