class Solution {
public:
    int maxNonOverlapping(vector<int>& nums, int target) {
        int n = nums.size();
        long sum = 0;
        unordered_map<int,int> mp;  //to track prefix-sum
        vector<int> dp(n,-2);       //to store index where interval starts
        mp[0] = -1;
        for(int i = 0;i < n; i++){
            sum += nums[i];
            if(mp.count(sum - target)){
                dp[i] = mp[sum - target];
            }
            mp[sum] = i;
        }
        int res = 0,pre = -2;
        for(int i = 0;i < n; i++){       //interval is {dp[i] + 1, i}
            if(dp[i] != -2 && pre < dp[i] + 1){ //count not overlapping intervals
                res++;
                pre = i;
            }
        }
        return res;
    }
};
