public:
    int maxNonOverlapping(vector<int>& nums, int target){
        int n = nums.size();
        map<int , bool > mp;
        vector<int> pre(n) , ans(n);
        for(int i = 0 ; i < n  ;i++)ans[i] = 0;
        pre[0] = nums[0];
        mp[pre[0]] = true;
        mp[0]=true;
        if(pre[0] == target){ans[0] = 1;pre[0] = 0;mp[0]=true;}
