                pre[i] = 0;
                mp.clear();
                mp[0] = true;
                ans[i] = 1 + ans[i-1];
            }
            else{
            ans[i] = ans[i-1];
            mp[pre[i]] = true;
            }
        }
        return ans[n-1];
    }
};
