        cost = float('inf')

        for i in range(left, right + 1):
            left_cost = self.solve(start_stick, cuts[i], cuts, left, i - 1, dp)
            right_cost = self.solve(cuts[i], end_stick, cuts, i + 1, right, dp)
            curr_cost = (end_stick - start_stick) + left_cost + right_cost
