	class Solution {
	public:
		int f(int i,int j,vector<int>& cuts){
			if(i>j) return 0;
			int mini=1e9;
			for(int ind=i;ind<=j;ind++){
				int cost= cuts[j+1]-cuts[i-1]+f(i,ind-1,cuts)+f(ind+1,j,cuts);
				mini=min(mini,cost);
			}
			return mini;
		}

