				if(i>j) return 0;
				if(dp[i][j]!=-1) return dp[i][j];
				int mini=1e9;
				for(int ind=i;ind<=j;ind++){
					int cost= cuts[j+1]-cuts[i-1]+f(i,ind-1,cuts,dp)+f(ind+1,j,cuts,dp);
					mini=min(mini,cost);
