				cuts.insert(cuts.end(),n);
				sort(cuts.begin(),cuts.end());
				int m=cuts.size();
				vector<vector<int>> dp(m+1,vector<int>(m+1,-1));
				return f(1,cuts.size()-2,cuts,dp);
