class Solution {
public:
    int minCost(int n, vector<int>& cuts) {
        // Append the starting and ending points to the cuts vector
        cuts.push_back(0);
        cuts.push_back(n);
        int m = cuts.size();

        // Sort the cuts vector in ascending order
        sort(cuts.begin(), cuts.end());

        // Create a 2D vector 'dp' to store computed results, initialized with -1
        vector<vector<int>> dp(m, vector<int>(m, -1));

        // Define a lambda function 'cost' to calculate the minimum cost of cutting
        function<int(int, int)> cost = [&](int i, int j) -> int {
            // Base case: if the distance between i and j is less than or equal to 1, no cuts are needed
            if (j - i <= 1)
                return 0;

            // Check if the result for current i and j has already been computed
            if (dp[i][j] != -1)
                return dp[i][j];

            int result = INT_MAX;

            // Iterate over possible cut positions between i+1 and j-1
            for (int k = i + 1; k < j; k++) {
                // Calculate the cost of cutting from i to k and from k to j recursively
                int cutCost = cost(i, k) + cost(k, j);

                // Update the minimum cost
                result = min(result, cutCost);
            }

            // Store the minimum cost for the current i and j in the 'dp' vector
            // Add the length of the current cut (cuts[j] - cuts[i]) to the result
            return dp[i][j] = result + (cuts[j] - cuts[i]);
        };

        // Start the recursive calculation from position 0 to position m-1
        return cost(0, m - 1);
    }
};

