        Arrays.sort(cuts);
        int c = cuts.length;
        List<Integer> cuts_list = new ArrayList<>();
        // add 0 in a list
        cuts_list.add(0);
        // add all the cuts element in list
        for (int i=0;i<c;i++) cuts_list.add(cuts[i]);
        // add n in a list
        cuts_list.add(n);
        dp = new int[c+2][c+2];
        for (int i=0;i<c+2;i++) Arrays.fill(dp[i],-1);
        return helper_mincost_func(1,c,cuts_list);


    }



    private int helper_mincost_func(int i,int j,List<Integer> cuts_list){
        if (i>j) return 0;

        if (dp[i][j] != -1) return dp[i][j];

        int mini = Integer.MAX_VALUE;
        for (int ind=i;ind<=j;ind++){
            int steps = cuts_list.get(j+1) - cuts_list.get(i-1) 
                + helper_mincost_func(i,ind-1,cuts_list)   // for left side 
                + helper_mincost_func(ind+1,j,cuts_list);  // for right side
            mini = Math.min(steps,mini);
        }
        return dp[i][j] = mini;
    }


```

