	cuts.insert(cuts.begin(), 0);
	cuts.push_back(n);
	sort(cuts.begin(), cuts.end());
	n = cuts.size();
	vector<vector<int>> dp(cuts.size(), vector<int>(n - 1, 0));
	for(int x = 1; x < n - 1; x++) {
