class Solution {
	public int minCost(int n, int[] cuts) {
//         int c = cuts.length;
//         int[] nCuts = new int[c + 2];
//         nCuts[0] = 0;
//         nCuts[c + 1] = n;
//         System.arraycopy(cuts, 0, nCuts, 1, c);
//         Arrays.sort(nCuts);

//         int[][] dp = new int[c + 1][c + 1];
//         for (int[] d : dp) Arrays.fill(d, -1);
//         return solRecursion(1, c, nCuts, dp);

		return solTabular(n, cuts);
	}

	private static int solTabular(int n, int[] cuts) {
		int c = cuts.length;
		int[] nCuts = new int[c + 2];
		nCuts[0] = 0;
		nCuts[c + 1] = n;
		System.arraycopy(cuts, 0, nCuts, 1, c);
		Arrays.sort(nCuts);
		int[][] dp = new int[c + 2][c + 2];

		// Base Case
		// ------

		// Simulating The Recursion
		for (int i = c; i >= 1; i--) {
			for (int j = i; j <= c; j++) {
				int mini = Integer.MAX_VALUE;
				for (int cut = i; cut <= j; cut++) {
					// Adding The Cost
					int cost = nCuts[j + 1] - nCuts[i - 1];
					// Adding The Cost Of The Sub Problems
					cost += dp[i][cut - 1] + dp[cut + 1][j];
					mini = Math.min(mini, cost);
				}
				dp[i][j] = mini;
			}
		}
		return dp[1][c];
	}

	private static int solRecursion(int i, int j, int[] cuts, int[][] dp) {
		// Base Case
		if (i > j) return 0;

		// Using DP
		if (dp[i][j] != -1) return dp[i][j];

		int mini = Integer.MAX_VALUE;
		for (int cut = i; cut <= j; cut++) {
			// Adding The Cost
			int cost = cuts[j + 1] - cuts[i - 1];
			// Adding The Cost Of The Sub Problems
			cost += solRecursion(i, cut - 1, cuts, dp) +
					solRecursion(cut + 1, j, cuts, dp);
			mini = Math.min(mini, cost);
		}
		return dp[i][j] = mini;
	}
}

