        }
        cut[size+1]=n;

        int dp[][]=new int[size+3][size+3];
        for(int y[]:dp)
        Arrays.fill(y,-1);

        return helper(1,size,cut,dp);
    }

    public int helper(int start,int end,int cut[],int dp[][])
    {
        if(start>end)
        return 0;

        if(dp[start][end]!=-1)
        return dp[start][end];

        int ans=cut[end+1]-cut[start-1]; 
        int min=Integer.MAX_VALUE;

        for(int i=start;i<=end;i++)
        {
            int left=helper(start,i-1,cut,dp);
            int right=helper(i+1,end,cut,dp);

            min=Math.min(min,left+right);
        }

        ans+=min;

        dp[start][end]=ans;

        return ans;
    }
}
