class Solution {
public:
    int f ( int i , int j , vector<int> &cuts , vector<vector<int>> &dp){
        // the base case if i > j means there can be no more partition , return 0
        if ( i > j ) return 0;
        if ( dp[i][j] != -1) return dp[i][j];
        int mn = INT_MAX;
        for ( int ind = i ; ind <= j ; ind ++){
            int cost = ( cuts.at(j + 1) - cuts.at(i - 1)) + f( i , ind - 1 , cuts ,dp ) + f( ind + 1 , j , cuts , dp);
            mn = min ( mn , cost );
        }

        return dp[i][j] = mn;
    }
    int minCost(int n, vector<int>& cuts) {
         // this is a nice question 
        // here we will add 0 and n to cuts
