    int help(int s, int e, vector<int>&cuts, map<pair<int,int>,int>&memo){
        if(s>=e) return 0;
        if(memo.count({s,e})) return memo[{s,e}];
        int ans = INT_MAX;
        for(int i =0;i<cuts.size();i++){
            if(cuts[i]>s && cuts[i]<e){
                int t = e-s + help(s,cuts[i],cuts,memo) + help(cuts[i],e,cuts,memo);
                ans = min(t,ans);
                // cout<<ans<<"   ";
            }
        }
        memo[{s,e}] = ans==INT_MAX ? 0:ans;
        return ans==INT_MAX ? 0:ans;

    }
    int minCost(int n, vector<int>& cuts) {
         map<pair<int,int>,int> memo;
        return help(0,n,cuts,memo);
    }
};
