class Solution {
public:
    int minCost(int n, vector<int>& cuts) {

        cuts.insert(cuts.begin(),0); // inserting  index 0 at front
        cuts.push_back(n);   // pushing n to the end.. we push these to maintain the boundaries
        sort(cuts.begin(),cuts.end());  // order of edges
        int m=cuts.size();
        vector<vector<int>> dp(m,vector<int>(m,0));


        for(int sz=2;sz<=m-1;sz++){
            for(int i=0;i<m-sz;i++){
                int j=i+sz;   // end of current window decided by i and sz

                dp[i][j]=INT_MAX;
                for(int k=i+1;k<j;k++){
                    dp[i][j]=min(dp[i][j],dp[i][k]+dp[k][j]+cuts[j]-cuts[i]);  // if   0  2   3 4  is the current window..then first we check the cost of [0,2] then [3,4]  and since we are making the cut on [0,4] we add the current length..which is cuts[j] - cuts[i]..
					// we have to take the minimum of all these cases ..in the above example we placed the cut at index k=i+1=1(value 2) now we place it at k=i+2(value 3)
                }
            }
        }
        return dp[0][m-1];

    }
};
